parameters.html:
=<!DOCTYPE html>
<html>
  <head>
    <title>Bảng lương</title>
    <style>
      body {
        background-color: #f5f5f5;
        font-family: Arial, sans-serif;
      }
      h1 {
        text-align: center;
        margin-top: 2rem;
        margin-bottom: 2rem;
        color: #333;
        text-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
      }
      table {
        border-collapse: collapse;
        width: 100%;
        margin: 0 auto;
        margin-bottom: 1rem;
        box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
        background: linear-gradient(to bottom, #f2f2f2, #fff);
      }
      th,
      td {
        padding: 1rem;
        border: 1px solid #ddd;
        text-align: center;
        color: #333;
      }
      tr:nth-child(even) {
        background-color: #e0e0e0;
      }
      tr:nth-child(odd) {
        background-color: #f5f5f5;
      }
      .logo {
        display: block;
        margin: 0 auto;
        max-width: 200px;
      }
      .container {
        text-align: center;
      }
    </style>
 </head>
  <body>
    <div class="container">
      <img src="https://media.net.vn/logo-ngang.png" alt="Logo công ty" class="logo">
      <h1>Bảng lương của {{ $('Webhook').item.json["query"]["name"] }}</h1>
      <table>
        <tbody>
        {{ $('Code').item.json["htmlSalaryFix"] }}
        {{ $('Code').item.json["htmlSalaryBHXH"] }}
        {{ $('Code').item.json["htmlTraffic"] }}
        {{ $('Code').item.json["htmlMultiplier"] }}
        {{ $('Code').item.json["htmlSalaryKD"] }}
        {{ $('Code').item.json["htmlSalaryTN"] }}
        {{ $('Code').item.json["htmlOther"] }}
        {{ $('Code').item.json["htmlChuyenCan"] }}
        {{ $('Code').item.json["htmlNote"] }}
        {{ $('Code').item.json["htmlSalaryTotal"] }}
        </tbody>
      </table>
    </div>
  </body>
</html>

