// Add a new field called 'myNewField' to the JSON of the item
const config = { style: 'currency', currency: 'VND'}
$input.item.json.htmlTraffic = ""
if ($('Webhook').item.json.query.Traffic) {
  $input.item.json.htmlTraffic = "<tr><th>Traffic</th><td>" + new Intl.NumberFormat('vi-VN').format($('Webhook').item.json.query.Traffic) + "</td></tr>"
}
$input.item.json.htmlMultiplier = ""
if ($('Webhook').item.json.query.multiplier) {
  $input.item.json.htmlMultiplier = "<tr><th>Hệ số</th><td>" + $('Webhook').item.json.query.multiplier + "</td></tr>"
}
$input.item.json.htmlOther = ""
if ($('Webhook').item.json.query.other) {
  $input.item.json.htmlOther = "<tr><th>Khác</th><td>" + new Intl.NumberFormat('vi-VN', config).format($('Webhook').item.json.query.other) + "</td></tr>"
}
$input.item.json.htmlSalaryKD = ""
if ($('Webhook').item.json.query.salaryKD) {
  $input.item.json.htmlSalaryKD = "<tr><th>Lương Kinh Doanh</th><td>" + new Intl.NumberFormat('vi-VN', config).format($('Webhook').item.json.query.salaryKD.replaceAll("[","").replaceAll("]","")) + "</td></tr>"
}
$input.item.json.htmlSalaryTN = ""
if ($('Webhook').item.json.query.salaryTN) {
  $input.item.json.htmlSalaryTN = "<tr><th>Lương Trách Nhiệm</th><td>" + new Intl.NumberFormat('vi-VN', config).format($('Webhook').item.json.query.salaryTN) + "</td></tr>"
}
$input.item.json.htmlSalaryBHXH = ""
if ($('Webhook').item.json.query.BHXH) {
  $input.item.json.htmlSalaryBHXH = "<tr><th>BHXH</th><td>" + new Intl.NumberFormat('vi-VN', config).format($('Webhook').item.json.query.BHXH) + "</td></tr>"
}
$input.item.json.htmlChuyenCan = ""
if ($('Webhook').item.json.query.chuyencan) {
  $input.item.json.htmlChuyenCan = "<tr><th>Chuyên Cần</th><td>" + new Intl.NumberFormat('vi-VN', config).format($('Webhook').item.json.query.chuyencan) + "</td></tr>"
}
$input.item.json.htmlNote = ""
if ($('Webhook').item.json.query.chuyencan) {
  $input.item.json.htmlNote = "<tr><th>Ghi chú</th><td>" + $('Webhook').item.json.query.note + "</td></tr>"
}

$input.item.json.htmlSalaryFix = "<tr><th>Lương cơ bản</th><td>" + new Intl.NumberFormat('vi-VN', config).format($('Webhook').item.json.query.salaryFix) + "</td></tr>"
$input.item.json.htmlSalaryTotal = "<tr><th>Tổng lương thực nhận</th><td>" + new Intl.NumberFormat('vi-VN', config).format($('Webhook').item.json.query.salaryTotal.replaceAll("[","").replaceAll("]","")) + "</td></tr>"



return $input.item;
