const data = $('Webhook').first().json.body.data[0]
let is_true_tx = !!data.description.indexOf('DCME')
let indexOfDCME = data.description.indexOf('DCME')
let tempText = data.description.slice(indexOfDCME,data.description.length)
let order_id = tempText.slice(0, data.description.indexOf(' ')+1 ? data.description.indexOf(' ') : data.description.length)
