const data = $('Webhook').first().json.body;
const fullname = data.fullname || '-';
const phone = data.phone || '-';
const source_title = data.source_title;
const source_url = data.source_url;
const message = data.message;

return {
  fullname,
  phone,
  message,
  source_title,
  source_url
}

const data = $("Code").first().json;

let elements = [];

elements.push({
    tag: "div",
    fields: [
        {
            is_short: true,
            text: {
                tag: "lark_md",
                content: `**Họ và tên:** ${data.fullname}`,
            }
        },
        {
            is_short: true,
            text: {
                tag: "lark_md",
                content: `**Số điện thoại:** ${data.phone}`,
            }
        },
        {
            is_short: false,
            text: {
                tag: "lark_md",
                content: `**Lời nhắn:** ${data.message}`,
            }
        }
    ],
});

// Add action buttons
elements.push({
    tag: "action",
    actions: [
        {
            tag: "button",
            text: {
                tag: "plain_text",
                content: "Chat Zalo cá nhân",
            },
            url: `https://zalo.me/${data.phone}`,
            type: "primary",
        },
        {
            tag: "button",
            text: {
                tag: "plain_text",
                content: "Gọi điện cá nhân",
            },
            url: `tel:${data.phone}`,
            type: "default",
        },
        {
            tag: "button",
            text: {
                tag: "plain_text",
                content: "Xem trên web",
            },
            url: `${data.source_url}`,
            type: "default",
        }
    ],
});


let result = {
    msg_type: "interactive",
    card: {
        config: {
            wide_screen_mode: true,
        },
        header: {
            template: "green",
            title: {
                content: `Yêu cầu tư vấn ${data.source_title}`,
                tag: "plain_text",
            },
        },
        elements,
    },
};
return { json: result };
