const dataFromBase = $('get_list_record').first().json.data
const tx = $('get_order_id_from_tx').first().json

// l·∫•y order object b·∫±ng order_id
const order = dataFromBase.items.find(forItem => forItem.fields.order_id === tx.order_id)

let isValidRecordId = !!order

// n·∫øu kh√¥ng c√≥ order th√¨ return false
if (!order) return {isValidRecordId}

// l·∫•y order_id t·ª´ order object
let order_id = order?.fields.order_id

// l·∫•y record_id b·∫£ng ghi t·ª´ order object
let record_id = order?.record_id

// l·∫•y email kh√°ch h√†ng
let email = order?.fields.email

// l·∫•y t·ªïng gi√° tr·ªã ƒë∆°n h√†ng
let moneyTotal = order?.fields.total

// l·∫•y s·ªë ti·ªÅn kh√°ch h√†ng ƒë√£ thanh to√°n
let rest = order?.fields.rest

// l·∫•y s·ªë ti·ªÅn c√≤n l·∫°i c·∫ßn thanh to√°n
// let rest = order?.fields.rest

// l·∫•y s·ªë ti·ªÅn v·ª´a nh·∫≠n ƒë∆∞·ª£c
let moneyReceive = tx.total_receive

// t√≠nh s·ªë ti·ªÅn c√≤n l·∫°i c·∫ßn thanh to√°n l·∫ßn n√†y: (s·ªë ti·ªÅn c√≤n l·∫°i c·∫ßn thanh to√°n) - (s·ªë ti·ªÅn nh·∫≠n ƒë∆∞·ª£c)
let moneyRest = rest - moneyReceive

// t√≠nh s·ªë ti·ªÅn ƒë√£ thanh to√°n
let moneyPayment = moneyTotal - moneyRest

// l·∫•y price t·ª´ order object
let price = order?.fields.price[0]

// l·∫•y amount t·ª´ order object
let amount = order?.fields.amount

// l·∫•y total t·ª´ order object
let total = order?.fields.total

// l·∫•y name t·ª´ order object
let name = order?.fields.name

// l·∫•y phone t·ª´ order object
let phone = order?.fields.phone

// l·∫•y address t·ª´ order object
let address = order?.fields.address

// l·∫•y size t·ª´ order object
let size = order?.fields.size

// l·∫•y style t·ª´ order object
let style = order?.fields.style

// t√¨nh tr·∫°ng thanh to√°n
let statusPayment = ''
if (moneyRest === 0) {
  statusPayment = 'completed'
} else if (moneyRest < 0) {
  statusPayment = 'overpayment'
} else {
  statusPayment = 'on-hold'
}

return {
  isValidRecordId,
  record_id,
  order_id,
  moneyReceive,
  moneyPayment,
  moneyRest,
  moneyTotal,
  price,
  amount,
  total,
  email,
  name,
  phone,
  address,
  size,
  style,
  statusPayment,
  order_data: order?.fields
}

