// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const salary_trial = $('Webhook').item.json.body.salary_trial;
const salary_trial_formatted = salary_trial.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");

const salary_offer = $('Webhook').item.json.body.salary_offer;
const salary_offer_formatted = salary_offer.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");

const hiring_time = $('Webhook').item.json.body.hiring_time;

// Chuyển đổi thành đối tượng Date
const date = new Date(hiring_time);

// Lấy giờ và phút
const hours = date.getHours();
const minutes = date.getMinutes();

// Lấy ngày, tháng và năm
const day = date.getDate();
const month = date.getMonth() + 1; // Tháng bắt đầu từ 0, nên cần +1
const year = date.getFullYear();

// Định dạng lại chuỗi kết quả
const formattedhiring_time = hours.toString().padStart(2, '0') + ':' + minutes.toString().padStart(2, '0') + ' ' + day.toString().padStart(2, '0') + '/' + month.toString().padStart(2, '0') + '/' + year;
return {'salary_trial': salary_trial_formatted, 'salary_offer': salary_offer_formatted, 'formattedhiring_time':formattedhiring_time};
