parameters.jsCode:
const allData = []

let counter = 0;

do {
  try {
    const data = $('Flatten list data').all(0, counter);
    allData.push(...data);
  } catch (error) {
    return allData.map(data => ({ json: data.json}));
  }

  counter++;
} while(true);

const reducedData = $('Merge data').all().reduce((acc, cur) => {
  return cur.json.total + acc
}, 0)

return {total: reducedData};

const now = new Date();

const firstDay = new Date(now.getFullYear(), now.getMonth(), 1);

let lastDay = new Date(now.getFullYear(), now.getMonth() + 1, 0);
lastDay.setSeconds(lastDay.getSeconds() - 1);

return (
  (items[0].json?.data && { data: items[0].json?.data }) || {
    data: {
      endpoint: `https://public.kiotapi.com/invoices?fromPurchaseDate=${firstDay.toISOString()}&toPurchaseDate=${lastDay.toISOString()}&orderBy=createdDate&orderDirection=asc`,
      currentItem: 0,
    },
  }
);

const result = $('Config request').all()[0].json;

result.data.currentItem += $('Invoices data').first().json.pageSize;

return result;

