// Loop over input items and add a new field
// called 'myNewField' to the JSON of each one
let startDate = new Date($('Schedule Trigger').first().json.timestamp);
let endDate = new Date($('Schedule Trigger').first().json.timestamp);

startDate.setHours(startDate.getHours() + 6);
// startDate.setHours(startDate.getHours() - 720); // fetch data previous 30 days
startDate.setMinutes(0);
startDate.setSeconds(1);

endDate.setHours(endDate.getHours() + 7); // Add 7 hrs to compensate for Caresoft time

return {
  json: {
    startDate: startDate.toISOString(),
    endDate: endDate.toISOString(),
  },
};

let input = $('Get call log in the last hour').first().json.calls;

// Get calls data
let records = input.map((call) => {
  let fields = {
    id: call.id,
    customer_id: call.customer_id,
    call_id: call.call_id,
    path: {
      text: "Xem cuộc gọi",
      link: call.path
    },
    path_download: {
      text:"Tải cuộc gọi",
      link: call.path_download
    },
    caller: call.caller,
    called: call.called,
    user_id: call.user_id,
    agent_id: call.agent_id.split(',').at(-1),
    agent_log: call.agent_id,
    group_id: call.group_id,
    call_type: call.call_type,
    start_time: new Date(call.start_time).getTime() - 3600000*7, // substract 7hrs
    call_status: call.call_status,
    end_time: new Date(call.end_time).getTime() - 3600000*7,
    wait_time: call.wait_time,
    hold_time: call.hold_time,
    talk_time: call.talk_time,
    end_status: call.end_status,
    ticket_id: call.ticket_id,
    last_agent_id: call.last_agent_id,
    last_user_id: call.last_user_id,
    call_survey: call.call_survey,
    call_survey_result: call.call_survey_result,
    missed_reason: call.missed_reason,
  };
  return { fields };
});

let result = { json: { records } };
return result;

