const data = $("Webhook").first().json;

// Order Info
const orderId = data.body.id;
const source = data.headers["x-wc-webhook-source"]
    .replace("http://", "")
    .replace("https://", "")
    .replace("www.", "")
    .split(/[/?#]/)[0]
    .toUpperCase();

const modifiedDate = new Date(data.body.date_modified).toLocaleString('vi-VN');

let orderStatus = "";
switch (data.body.status) {
    case "pending":
        orderStatus = "Đang chờ";
        break;
    case "processing":
        orderStatus = "Đang xử lý";
        break;
    case "on-hold":
        orderStatus = "Giữ";
        break;
    case "completed":
        orderStatus = "Hoàn thành";
        break;
    case "cancelled":
        orderStatus = "Đã huỷ";
        break;
    case "refunded":
        orderStatus = "Hoàn tiền";
        break;
    case "failed":
        orderStatus = "Thất bại";
        break;

    case "trash":
        orderStatus = "Đã xoá";
        break;

    default:
        orderStatus = "-";
}

const paymentMethod = data.body.payment_method_title;
const discount =
  Math.floor(+data.body.discount_total).toLocaleString("en-US") + " VND";
const total = Math.floor(+data.body.total).toLocaleString("en-US") + " VND";

//Client info
const customerName = `${data.body.billing.last_name} ${data.body.billing.first_name}`;
const customerAddr = `${data.body.billing.address_1},  ${data.body.billing.city}`;
const customerEmail = data.body.billing.email;
const customerPhone = data.body.billing.phone;
const customerNote = data.body.customer_note;

// Product info
const products = data.body.line_items.map((product) => {
    const result = {
        name: product.name,
        quantity: product.quantity,
        price: product.price.toLocaleString("en-US") + " VND",
        productTotal:
            Math.floor(+product.total).toLocaleString("en-US") + " VND",
    };

    return result;
});

let result = {
    orderId,
    source,
    modifiedDate,
    orderStatus,
    paymentMethod,
    discount,
    total,
    customerName,
    customerAddr,
    customerEmail,
    customerPhone,
    customerNote,
    products,
};

return result;

