                content: `**Ghi chú:** ${data.customerNote}`,
            },
        },
    ],
});
// elements.push({
//     tag:'hr'
//   })

// Add action buttons
elements.push({
    tag: "action",
    actions: [
        {
            tag: "button",
            text: {
                tag: "plain_text",
                content: "Xử lý đơn hàng",
            },
            url: `https://${data.source}/wp-admin/post.php?post=${data.orderId}&action=edit`,
            type: "primary",
        },
        {
            tag: "button",
            text: {
                tag: "plain_text",
                content: "Gọi điện tư vấn",
            },
            url: `tel:${data.customerPhone}`,
            type: "default",
        },
