  $input.item.json.buyerStore = $input.item.json.emailSubject.split(' from ')[1]
  //Gan json ten store
  $input.item.json.buyerStoreJsonLabel = "{\"is_short\":true,\"text\":{\"tag\":\"lark_md\",\"content\":\"**💈 Store:**\\n " + 
$input.item.json.buyerStore + "\"}},";
  //Gan json link ve store
  $input.item.json.buyerStoreJsonAction = "{\"tag\":\"button\",\"text\":{\"content\":\"View Shop\",\"tag\":\"lark_md\"},\"url\":\"https://etsy.com/shop/" + $input.item.json.buyerStore + "\",\"type\":\"default\",\"value\":{}}"
}

$input.item.json.buyerMess = $('Set').all()[0].json.emailContent.split('<https://www.etsy.com/conversations/')[2]
