//Get order Total
$input.item.json.orderTotal = $('Set').item.json.emailSubject.split('Etsy Order confirmation for: ')[1];
$input.item.json.orderTotal = $input.item.json.orderTotal.split(' ')[0];

//Get buyer Name
$input.item.json.buyerName = $('Set').item.json.emailSubject.split(' from: ')[1];
$input.item.json.buyerName = $input.item.json.buyerName.split(' (')[0];

//Get Order ID
$input.item.json.orderID = $('Set').item.json.emailSubject.split('(')[1];
$input.item.json.orderID = $input.item.json.orderID.split(')')[0];

//Get Orrder Quantity
$input.item.json.orderQuantity = $('Set').item.json.emailContent.split('Congratulations on your Etsy sale of ')[1];
$input.item.json.orderQuantity = $input.item.json.orderQuantity.split(' item')[0];

//Get Shop Name
$input.item.json.shopName = $('Set').item.json.emailContent.split('Shop: ')[1];
$input.item.json.shopName = $input.item.json.shopName.split('\n')[0];

//Get Buyer Name
$input.item.json.buyerName = $('Set').item.json.emailContent.split('<https://www.etsy.com/people/')[1];
$input.item.json.buyerName = $input.item.json.buyerName.split('?')[0];

//Get Buyer Email
$input.item.json.buyerEmail = $('Set').item.json.emailContent.split('Send them an email <')[1];
$input.item.json.buyerEmail = $input.item.json.buyerEmail.split('>')[0];
$input.item.json.buyerEmail = $input.item.json.buyerEmail.replaceAll('mailto:', '');

//Get Order Products list
$input.item.json.orderProducts = $('Set').item.json.emailContent.split('<https://www.etsy.com/transaction/');
//Remove first item
$input.item.json.orderProducts.shift();
let i = 0;
do {
  $input.item.json.orderProducts[i] = $input.item.json.orderProducts[i].split('>')[1];
  i++;
} while (i < $input.item.json.orderProducts.length);

//Get Order paid
$input.item.json.orderPaid = $input.item.json.orderProducts[$input.item.json.orderProducts.length - 1].split('Order total')[1];
//$input.item.json.orderPaid = $input.item.json.orderPaid.replaceAll('\n', ' ');
$input.item.json.orderPaid = $input.item.json.orderPaid.trim()

//Beauty last order item detail
$input.item.json.orderProducts[$input.item.json.orderProducts.length - 1] = $input.item.json.orderProducts[$input.item.json.orderProducts.length - 1].split('Order total')[0];
//Beauty title
i = 0;
do {
  $input.item.json.orderProducts[i] = $input.item.json.orderProducts[i].replaceAll('\n',' ');
  $input.item.json.orderProducts[i] = $input.item.json.orderProducts[i].trim();
  i+=2;
} while (i < $input.item.json.orderProducts.length);
//Beauty order detail
i = 1;
do {
  $input.item.json.orderProducts[i] = $input.item.json.orderProducts[i].split('Personalized item')[0];
  $input.item.json.orderProducts[i] = $input.item.json.orderProducts[i].trim();
  let index01 = $input.item.json.orderProducts[i].indexOf("Shop");
  index01 = $input.item.json.orderProducts[i].substring(0,index01)
  let index02 = $input.item.json.orderProducts[i].indexOf("Quantity:");
  index02 = $input.item.json.orderProducts[i].substring(index02,$input.item.json.orderProducts[i].length)
  $input.item.json.orderProducts[i] = index01 + index02;
  i+=2;
} while (i < $input.item.json.orderProducts.length);

//Merch order detail to string
$input.item.json.orderProductDetail = '';
i = 0;
do {
  $input.item.json.orderProductDetail += $input.item.json.orderProducts[i] + '\n' + $input.item.json.orderProducts[i+1] + '\n \n ';
  i+=2;
} while (i < $input.item.json.orderProducts.length);
$input.item.json.orderProductDetail = $input.item.json.orderProductDetail.replaceAll('\n', '\\n');
$input.item.json.orderProductDetail = $input.item.json.orderProductDetail.trim()

$input.item.json.orderProductDetail = $input.item.json.orderProductDetail.split('No returns or exchanges');
i = 0;
do {
  if ($input.item.json.orderProductDetail[i].includes(']')) {
    $input.item.json.orderProductDetail[i] = $input.item.json.orderProductDetail[i].split(']')[1]
    $input.item.json.orderProductDetail[i] = $input.item.json.orderProductDetail[i].trim()
  }
  i+=1;
} while (i < $input.item.json.orderProductDetail.length);
//Remove last items
$input.item.json.orderProductDetail.pop()

return $input.item;

//Get Ship date
$input.item.json.shipDate = $('Set').all()[0].json.emailSubject.split('Ship by ')[1];
$input.item.json.shipDate = $input.item.json.shipDate.split(' - [')[0];

//Get Order ID
