  $input.item.json.shippingAdress = $('Set').all()[0].json.emailContent.split('Shipping address')[1];
  $input.item.json.shippingAdress = $input.item.json.shippingAdress.replaceAll('\n', ' ');
  $input.item.json.shippingAdress = $input.item.json.shippingAdress.replaceAll('*', '');
  $input.item.json.shippingAdress = $input.item.json.shippingAdress.trim();
  $input.item.json.shippingAdress = $input.item.json.shippingAdress.split('United States')[0];
  $input.item.json.shippingAdress = $input.item.json.shippingAdress.trim();
  $input.item.json.shippingAdress = $input.item.json.shippingAdress + ", US"
