const data = $('Webhook').first().json.body;

let ten_ve = data.ten_ve || '-';
ten_ve = ten_ve.replace(/\n/g, ", ");

let fullname = data.fullname || '-';
fullname = fullname.replace(/\n/g, ", ");

let phone = data.phone || '-';
phone = phone.replace(/\n/g, ", ");

let sdtZalo = '84' + phone.slice(1);

let nhieu_thong_tin = false;
if (data.nhieu_thong_tin !== '') nhieu_thong_tin = true;

let so_chieu = 2;
if (data.so_chieu == 'Một chiều') so_chieu = 1;

let ngay_khoi_hanh = (data.ngay_khoi_hanh === '') ? '-' : new Date(data.ngay_khoi_hanh).toLocaleDateString('en-GB');

let ngay_tro_ve = (data.ngay_tro_ve === '') ? '-' : new Date(data.ngay_tro_ve).toLocaleDateString('en-GB');

let so_luong_khach = data.so_luong_khach || 0;

let hang = data.hang || '-';
hang = hang.replace(/\n/g, ", ");

let noi_khoi_hanh = data.noi_khoi_hanh || '-';
noi_khoi_hanh = noi_khoi_hanh.replace(/\n/g, ", ");

let diem_den = data.diem_den || '-';
diem_den = diem_den.replace(/\n/g, ", ");

let chatcode = Math.floor(Math.random() * 101);

const source_title = data.source_title;
const source_url = data.source_url;

if (nhieu_thong_tin === false) {
  ngay_khoi_hanh = '-';
  ngay_tro_ve = '-';
  hang = '-';
  so_chieu = '-';
  so_luong_khach = '-';
  noi_khoi_hanh = '-';
  diem_den = '-';
}

if (so_chieu === 1) {
  ngay_tro_ve = '-';
}
return {
  chatcode,
  fullname,
  phone,
  sdtZalo,
  nhieu_thong_tin,
  ten_ve,
  so_chieu,
  ngay_khoi_hanh,
  ngay_tro_ve,
  so_luong_khach,
  hang,
  noi_khoi_hanh,
  diem_den,
  source_title,
  source_url
}

