const dataFromBase = $('get_list_record').first().json.data
const tx = $('get_order_id_from_tx').first().json

// lấy order object bằng order_id
const order = dataFromBase.items.find(forItem => forItem.fields.order_id === tx.order_id)

let isValidRecordId = !!order

// nếu không có order thì return false
if (!order) return {isValidRecordId}

// lấy order_id từ order object
let order_id = order?.fields.order_id

// lấy record_id bảng ghi từ order object
let record_id = order?.record_id

// lấy email khách hàng
let email = order?.fields.email

// lấy tổng giá trị đơn hàng
let moneyTotal = order?.fields.total

// lấy số tiền khách hàng đã thanh toán
let rest = order?.fields.rest

// lấy số tiền còn lại cần thanh toán
// let rest = order?.fields.rest

// lấy số tiền vừa nhận được
let moneyReceive = tx.total_receive

// tính số tiền còn lại cần thanh toán lần này: (số tiền còn lại cần thanh toán) - (số tiền nhận được)
let moneyRest = rest - moneyReceive

// tính số tiền đã thanh toán
let moneyPayment = moneyTotal - moneyRest

// lấy price từ order object
let price = order?.fields.price[0]

// lấy amount từ order object
let amount = order?.fields.amount

// lấy total từ order object
let total = order?.fields.total

// lấy name từ order object
let name = order?.fields.name

// lấy phone từ order object
let phone = order?.fields.phone

// lấy address từ order object
let address = order?.fields.address

// lấy size từ order object
let size = order?.fields.size

// lấy style từ order object
let style = order?.fields.style

// tình trạng thanh toán
let statusPayment = ''
if (moneyRest === 0) {
  statusPayment = 'completed'
} else if (moneyRest < 0) {
  statusPayment = 'overpayment'
} else {
  statusPayment = 'on-hold'
}

return {
  isValidRecordId,
  record_id,
  order_id,
  moneyReceive,
  moneyPayment,
  moneyRest,
  moneyTotal,
  price,
  amount,
  total,
  email,
  name,
  phone,
  address,
  size,
  style,
  statusPayment,
  order_data: order?.fields
}

