while (countNgayLamViec < soNgayLamViec) {
  ngayNhanHangDuKien.setDate(ngayNhanHangDuKien.getDate() + 1); // Tăng ngày lên 1
  const dayOfWeek = ngayNhanHangDuKien.getDay(); // Lấy số thứ tự của ngày trong tuần (0: Chủ nhật, 1: Thứ 2, 2: Thứ 3, ..., 6: Thứ 7)
  if (dayOfWeek > 0 && dayOfWeek < 6) { // Kiểm tra nếu là ngày làm việc (từ thứ 2 đến thứ 6)
    countNgayLamViec++;
  }
}

