  switch (bankName) {
    case 'vietcombank':
      regex = /\d{2}-\d{2}-\d{4} \d{2}:\d{2}:\d{2}/;
      match = msg.match(regex);

      if (match) {
        // Convert text to timestamp
        // Split the time text into date and time parts
        const [datePart, timePart] = match[0].split(' ');

        // Split the date part into day, month, and year
        const [day, month, year] = datePart.split('-');

        // Combine the date parts into a valid date string (in YYYY/MM/DD format)
        const dateString = `${year}/${month}/${day} ${timePart}`;

        // Create a Date object from the valid date string
        const dateObj = new Date(dateString);

        // Get the timestamp in UTC
        const timestamp = dateObj.getTime() - 7 * 60 * 60 * 1000;

