function getTransactionType(msg, bankName) {
  let regex;
  let match;
  let transactionType = {
    type: '-1',
    typeText: '-',
  };

  switch (bankName) {
    case 'vietcombank':
      regex = /SD TK \d+ ([+-])/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    case 'vpbank':
      regex = /([\+\-])/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    case 'tpbank':
      regex = /PS:([\+\-])/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    case 'vib':
      regex = /PS:([\+\-])/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    case 'seabank':
      regex = /phat sinh ([\+\-])/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    case 'bidv':
      regex = /([\+\-])/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    case 'ocb':
      regex = /\(([\+\-])\)/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    case 'techcombank':
      regex = /([\+\-])/;
      match = msg.match(regex);
      if (match) {
        transactionType = {
          type: match[1],
          typeText: match[1] === '+' ? 'credit' : 'debit',
        };
      }
      return transactionType;

    default:
      return transactionType;
  }
}

function getTransactionAmount(msg, bankName) {
  let regex;
  let match;
  let transactionAmount = {
    amount: -1,
    amountText: '-',
  };

  switch (bankName) {
    case 'vietcombank':
      regex = /SD TK \d+ [+-]?(\d{1,3}(,\d{3})*?)VND/;
      match = msg.match(regex);

      if (match) {
        transactionAmount = {
          amount: parseFloat(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    case 'vpbank':
      regex = /[\+\-](\d{1,3}(?:,\d{3})*)VND/;
      match = msg.match(regex);
      if (match) {
        transactionAmount = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    case 'tpbank':
      regex = /PS:[+-]([\d.,]+)VND/;
      match = msg.match(regex);

      if (match) {
        transactionAmount = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    case 'vib':
      regex = /PS:[+-]([\d.,]+)/;
      match = msg.match(regex);

      if (match) {
        transactionAmount = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    case 'seabank':
      regex = /phat sinh [+-]([\d.,]+)/;
      match = msg.match(regex);

      if (match) {
        transactionAmount = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    case 'bidv':
      regex = /[+-]([\d.,]+)VND/;
      match = msg.match(regex);

      if (match) {
        transactionAmount = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    case 'ocb':
      regex = /\([\+\-]\) ([\d.,]+) VND/;
      match = msg.match(regex);

      if (match) {
        transactionAmount = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    case 'techcombank':
      regex = /[\+\-]([\d.,]+)/;
      match = msg.match(regex);

      if (match) {
        transactionAmount = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return transactionAmount;

    default:
      return transactionAmount;
  }
}

function getBalance(msg, bankName) {
  let regex;
  let match;
  let balance = {
    amount: -1,
    amountText: '-',
  };

  switch (bankName) {
    case 'vietcombank':
      regex = /(?<=SD )\d{1,3}(,\d{3})*?(?=VND)/;
      match = msg.match(regex);
      if (match) {
        balance = {
          amount: parseFloat(match[0].replace(/[\.\,]+/g, '')),
          amountText: match[0],
        };
      }
      return balance;

    case 'vpbank':
      regex = /So du\s?(\d{1,3}(?:,\d{3})*)VND/;
      match = msg.match(regex);
      if (match) {
        balance = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return balance;

    case 'tpbank':
      regex = /SD:\s?([\d.,]+)VND/;
      match = regex.exec(msg);
      if (match) {
        balance = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return balance;

    case 'vib':
      regex = /SODU:[+-]([\d.,]+)/;
      match = regex.exec(msg);
      if (match) {
        balance = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return balance;

    case 'seabank':
      regex = /SD:\s?([\d.,]+)VND/;
      match = regex.exec(msg);
      if (match) {
        balance = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return balance;

    case 'bidv':
      regex = /So du:([\d.,]+)VND/;
      match = regex.exec(msg);
      if (match) {
        balance = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return balance;

    case 'ocb':
      regex = /So du:\s?([\d.,]+) VND/;
      match = regex.exec(msg);
      if (match) {
        balance = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return balance;

    case 'techcombank':
      regex = /So du:\s?([\d.,]+)/;
      match = regex.exec(msg);
      if (match) {
        balance = {
          amount: Number(match[1].replace(/[\.\,]+/g, '')),
          amountText: match[1],
        };
      }
      return balance;

    default:
      return balance;
  }
}

function getTransactionId(msg, bankName) {
  let regex;
  let match;
  let transactionId;

  switch (bankName) {
    case 'vietcombank':
      regex = /Ref (\d{1,}\.\d{1,}\.\d{1,})/;
      match = msg.match(regex);

      if (match) {
        transactionId = match[1];
      }
      return transactionId;

    case 'vpbank':
      return transactionId;

    case 'tpbank':
      regex = /ND:\s*([A-Z0-9.-]+B-11\.)/;
      match = msg.match(regex);

      if (match) {
        return match[1];
      }
      return transactionId;

    case 'vib':
      return transactionId;

    case 'seabank':
      return transactionId;

    case 'bidv':
      return transactionId;

    case 'ocb':
      return transactionId;

    case 'techcombank':
      return transactionId;

    default:
      return transactionId;
  }
}

function getTransactionMessage(msg, bankName) {
