        cardColor: 'violet',
      };
      return larkMessConfig;

    case 'vib':
      larkMessConfig = {
        cardColor: 'orange',
      };
      return larkMessConfig;

    case 'seabank':
      larkMessConfig = {
        cardColor: 'red',
      };
