// Lấy nội dung văn bản đầu vào từ n8n
const inbut = $("Gmail").first().json;
const inbutText = inbut.text;
const inputText = inbut.html;
// Sử dụng biểu thức chính quy để trích xuất các thông tin
const nameMatch = inputText.match(/<div style="color:#050505;font-family:SF Pro Text, Helvetica Neue,Helvetica,Lucida Grande,tahoma,verdana,arial,sans-serif;font-size:12px;">([^<]*)<\/div>/);
const amountMatch = inputText.match(/<div style="font-size: 28px;line-height: 32px;color: #050505; padding-top: 2px; font-weight: 600;" class="mb_inl">([^<]*)<\/div>/);
const currencyMatch = inputText.match(/\(([^)]*)\)<\/div><\/td>/);
const adIdMatch = inputText.match(/txid=([^&"]+)/);
const adIdLink = inputText.match(/<a href="([^"]*)/)
const dateRangeMatch = inputText.match(/<div style="font-size: 13px;line-height: 16px;color: #050505;" class="mb_inl">([^<]*)/);
const invoiceReasonMatch = inbutText.match(/Lý do lập hóa đơn: ([^₫]*)./);
const productTypeMatch = inbutText.match(/Loại sản phẩm: ([^Lý]*)./);
const paymentMethodMatch = inbutText.match(/PHƯƠNG THỨC THANH TOÁN: ([^Số]*)./);
const referenceNumberMatch = inbutText.match(/Số tham chiếu: ([^ Chiến]*)/);
const invoiceNumberMatch = inputText.match(/Số hóa đơn: ([^<]*)/);
const customerMatch = inputText.match(/Khách hàng: ([^<]*)/);
const addressMatch = inputText.match(/Địa chỉ: ([^<]*)/);
// Lập trình thêm các biểu thức chính quy khác để trích xuất các thông tin còn lại

// Trích xuất thông tin từ các kết quả khớp
const accountName = nameMatch && nameMatch[1];
const amount1 = amountMatch && amountMatch[1];
//Đổi tiền thành số
const amountString = amount1;
const amountWithoutCurrency = amountString.replace(/[^\d-]/g, "");
const amount = Math.floor(parseFloat(amountWithoutCurrency));
const currency = currencyMatch && currencyMatch[1].trim();
//tiếp tục
const adId = adIdMatch && adIdMatch[1];
const adURL = adIdLink && adIdLink[1];
const dateRange = dateRangeMatch && dateRangeMatch[1];
const invoiceReason = invoiceReasonMatch && invoiceReasonMatch[1];
const productType = productTypeMatch && productTypeMatch[1];
const paymentMethod = paymentMethodMatch && paymentMethodMatch[1];
const referenceNumber = referenceNumberMatch && referenceNumberMatch[1];
const invoiceNumber = invoiceNumberMatch && invoiceNumberMatch[1];
const customer = customerMatch && customerMatch[1];
const address = addressMatch && addressMatch[1];
// Lập trình thêm các biến để lưu trữ các thông tin còn lại

// Tạo đối tượng JSON để chứa tất cả các thông tin trích xuất
const fields = {
  "TenTaiKhoanQC": accountName,
  "SoTien": amount,
  "Currency": currency,
  "Idgiaodich": {"link": adURL,
          "text": adId},
  "KhoangNgay": dateRange,
  "LyDoLapHoaDon": invoiceReason,
  "LoaiSanPham": productType,
  "PhuongThucThanhToan": paymentMethod,
  "SoThamChieu": referenceNumber,
  // Lập trình thêm các thông tin còn lại vào đây
};

// Trả về đối tượng JSON chứa tất cả thông tin trích xuất
const records = [];
records.push({ fields });
let result = { json: { records } };
return result;

