if (
  ($('getBankData').item.json.result.transactionType.type === '-' &&
    $input.item.json.enableOutgoing) ||
  ($('getBankData').item.json.result.transactionType.type === '+' &&
    $input.item.json.enableIncoming)
) {
  const data = $('getBankData').first().json.result;

  let elements = [];

  elements.push({
    tag: 'div',
    fields: [
      {
        is_short: false,
        text: {
          tag: 'lark_md',
          content: `**Ngân hàng**: ${data.bankName.toUpperCase()}`,
        },
      },
      {
        is_short: false,
        text: {
          tag: 'lark_md',
          content: `**Số tài khoản**: ${data.receiverAccount}`,
        },
      },
      {
        is_short: false,
        text: {
          tag: 'lark_md',
          content: `**Số dư thay đổi:** ${data.transactionType.type} ${data.transactionAmount.amountText} VND`,
        },
      },
      {
        is_short: false,
        text: {
          tag: 'lark_md',
          content: `**Nội dung:** ${data.transactionMessage}`,
        },
      },
    ],
  });

  // Append transaction ID if exist
  if (data.transactionId) {
    elements[0].fields.push({
      is_short: false,
      text: {
        tag: 'lark_md',
        content: `**Mã giao dịch:** ${data.transactionId}`,
      },
    });
  }

  // Append received time
  elements[0].fields.push({
    is_short: false,
    text: {
      tag: 'lark_md',
      content: `**Thời gian nhận:** ${data.receivedTime.text}`,
    },
  });

  // Kiểm tra enableBalance nếu true thì thêm phần tử "Số dư tạm tính" vào mảng fields
  if ($input.item.json.enableBalance) {
    const balanceIndex = elements[0].fields.findIndex((field) =>
      field?.text?.content?.includes('Số dư thay đổi')
    );
    if (balanceIndex !== -1) {
      elements[0].fields.splice(balanceIndex + 1, 0, {
        is_short: false,
        text: {
          tag: 'lark_md',
          content: `**Số dư tạm tính**: ${data.balance.amountText} VND`,
        },
      });
    }
  }

  // create message card
  let result = {
    msg_type: 'interactive',
    card: {
      config: {
        wide_screen_mode: true,
      },
      header: {
        template: data.larkMessConfig?.cardColor ?? 'blue',
        title: {
          content: `${data.bankName.toUpperCase()}-${data.receiverAccount} (${
            data.transactionType.type
          }) ${data.transactionAmount.amountText}VND`,
          tag: 'plain_text',
        },
      },
      elements,
    },
  };

  return {
    json: {
      isSend: true,
      data: result,
      customBotURL: $input.item.json.customBotURL,
    },
  };
}

return {
  json: {
    isSend: false,
  },
};

if (
  ($('getBankData').item.json.result.transactionType.type === '-' &&
    $input.item.json.enableOutgoing) ||
  ($('getBankData').item.json.result.transactionType.type === '+' &&
    $input.item.json.enableIncoming)
) {
  const data = $('getBankData').first().json.result;

  // Convert to Lark Base format
  const records = [
    {
      fields: {
        ID: data.bankName + '-' + data.receiverAccount,
        Bank: data.bankName.toUpperCase(),
        Account: data.receiverAccount,
        'Transaction Type': data.transactionType.typeText,
        Debit:
          data.transactionType.type === '+'
            ? data.transactionAmount.amount
            : undefined,
        Credit:
          data.transactionType.type === '-'
            ? data.transactionAmount.amount
            : undefined,
        'Transaction ID': data.transactionId,
        'Transaction Message': data.transactionMessage,
        'Original Message': data.originalMessage,
        'Time received': data.receivedTime.timestamp,
      },
    },
  ];

  // Kiểm tra enableBalance nếu true thì thêm phần tử "Balance" vào mảng fields
  if ($input.item.json.enableBalance) {
    records[0].fields = {
      ...records[0].fields,
      Balance: data.balance.amount,
    };
  }

  return {
    json: {
      isSend: true,
      records,
      configs: {
        baseId: $input.item.json.baseId,
        tableId: $input.item.json.tableId,
      },
    },
  };
}

return {
  json: {
    isSend: false,
  },
};

if (
  ($('getBankData').item.json.result.transactionType.type === '-' &&
    $input.item.json.enableOutgoing) ||
  ($('getBankData').item.json.result.transactionType.type === '+' &&
    $input.item.json.enableIncoming)
) {
  const data = $('getBankData').first().json.result;

  let message = [];

  message.push(
    `<b>${data.bankName.toUpperCase()}-${data.receiverAccount} (${
      data.transactionType.type
    }) ${data.transactionAmount.amountText}VND</b>\n`,
    `<b>Ngân hàng:</b> ${data.bankName.toUpperCase()}`,
    `<b>Số tài khoản:</b> ${data.receiverAccount}`,
    `<b>Số dư thay đổi:</b> ${data.transactionType.type} ${data.transactionAmount.amountText} VND`
  );

  // Add balance
  if ($input.item.json.enableBalance) {
    message.push(`<b>Số dư tạm tính:</b> ${data.balance.amountText} VND`);
  }

  message.push(`<b>Nội dung:</b>\n ${data.transactionMessage}`);

  // Check transaction ID
  if (data.transactionId) {
    message.push(`<b>Mã giao dịch:</b> ${data.transactionId}`);
  }

  message.push(`<b>Thời gian nhận:</b> ${data.receivedTime.text}`);

  return {
    json: {
      isSend: true,
      botToken: $input.item.json.botToken,
      payload: {
        chat_id: $input.item.json.chatId,
        parse_mode: 'HTML',
        text: message.join('\n'),
      },
    },
  };
}

return {
  json: {
    isSend: false,
  },
};

const smsContent = $('Webhook').first().json.body;

// create message card
let result = {
  msg_type: 'text',
  content: {
    text: `Từ: ${smsContent.from}\n${smsContent.text.toString()}`,
  },
};

return {
  json: {
    data: result,
    customBotURL: $input.item.json.customBotURL,
  },
};

const smsContent = $('Webhook').first().json.body;

let message = [];

message.push(
  `<b>${smsContent.from.toUpperCase()}</b>\n`,
  `${smsContent.text.toString()}`
);

return {
  json: {
    botToken: $input.item.json.botToken,
    payload: {
      chat_id: $input.item.json.chatId,
      parse_mode: 'HTML',
      text: message.join('\n'),
    },
  },
};

const smsContent = $('Webhook').first().json.body;

  // Convert to Lark Base format
  const records = [
    {
      fields: {
        ID: smsContent.from,
        Bank: smsContent.from,
        'Transaction Type': 'OTP',
        'Transaction Message': smsContent.text.toString(),
        'Original Message': smsContent.text.toString(),
        'Time received': smsContent.sentStamp,
      },
    },
  ];

  return {
    json: {
      records,
      configs: {
        baseId: $input.item.json.baseId,
        tableId: $input.item.json.tableId,
      },
    },
  };
