// Add a new field called 'myNewField' to the
// JSON of the item
$input.item.json.products = $('Shopify Trigger').item.json.line_items;
$input.item.json.orderItems = []
let pI = 0
let orderMoney = 0
let pLength = $input.item.json.products.length;
while (pI < pLength) {
  let person = {
    id:$input.item.json.products[pI].product_id, 
    quantity:$input.item.json.products[pI].quantity, 
    name:$input.item.json.products[pI].name,
    //code:$input.item.json.products[pI].sku, 
    //type:$input.item.json.products[pI].title, 
    price:$input.item.json.products[pI].price, 
    //weight:$input.item.json.products[pI].grams
  };
  orderMoney += $input.item.json.products[pI].quantity * $input.item.json.products[pI].price
  $input.item.json.orderItems[pI] = person;
  pI++;
}
$input.item.json.customerPhone = $('Shopify Trigger').item.json.billing_address.phone.replaceAll(" ","")
$input.item.json.orderMoney = 0
if ("Chuy·ªÉn Kho·∫£n Ng√¢n H√†ng Th·ªß C√¥ng" == $('Shopify Trigger').item.json.payment_gateway_names || "Thanh to√°n Alepay ATM,QRCode,Th·∫ª Qu·ªëc t·∫ø,Tr·∫£ g√≥p" == $('Shopify Trigger').item.json.payment_gateway_names) {
  $input.item.json.orderMoney = orderMoney;
  $input.item.json.paymentNote = "Check chuyen khoan truoc";
}
